export class RestrictionManager extends ReadWriteProxy {
    /**
     * @param {Application} app
     */
    constructor(app) {
        super(app, "restriction-flags.bin");

        this.currentData = this.getDefaultData();
    }

    // -- RW Proxy Impl

    /**
     * @param {any} data
     */
    verify(data) {
        return ExplainedResult.good();
    }

    /**
     */
    getDefaultData() {
        return {
            version: this.getCurrentVersion(),
            savegameV1119Imported: false,
        };
    }

    /**
     */
    getCurrentVersion() {
        return 1;
    }

    /**
     * @param {any} data
     */
    migrate(data) {
        return ExplainedResult.good();
    }

    initialize() {
        return this.readAsync().then(() => {
            if (this.currentData.savegameV1119Imported) {
                console.warn("Levelunlock is granted to current user due to past savegame");
            }
        });
    }

    // -- End RW Proxy Impl

    /**
     * Checks if there are any savegames from the 1.1.19 version
     */
    onHasLegacySavegamesChanged(has119Savegames = false) {
        if (has119Savegames && !this.currentData.savegameV1119Imported) {
            this.currentData.savegameV1119Imported = true;
            console.warn("Current user now has access to all levels due to 1119 savegame");
            return this.writeAsync();
        }
        return Promise.resolve();
    }

    /**
     * Returns if the app is currently running as the limited version
     * @returns {boolean}
     */
    isLimitedVersion() {
        if (IS_MAC) {
            // On mac, the full version is always active
            return false;
        }

        if (G_IS_STANDALONE) {
            // Standalone is never limited
            return false;
        }

        if (queryParamOptions.fullVersion) {
            // Full version is activated via flag
            return false;
        }

        if (G_IS_DEV) {
            return typeof window !== "undefined" && window.location.search.indexOf("demo") >= 0;
        }

        return true;
    }

    /**
     * Returns if the app markets the standalone version on steam
     * @returns {boolean}
     */
    getIsStandaloneMarketingActive() {
        return this.isLimitedVersion();
    }

    /**
     * Returns if exporting the base as a screenshot is possible
     * @returns {boolean}
     */
    getIsExportingScreenshotsPossible() {
        return !this.isLimitedVersion();
    }

    /**
     * Returns the maximum number of supported waypoints
     * @returns {number}
     */
    getMaximumWaypoints() {
        return this.isLimitedVersion() ? 2 : 1e20;
    }

    /**
     * Returns if the user has unlimited savegames
     * @returns {boolean}
     */
    getHasUnlimitedSavegames() {
        return !this.isLimitedVersion();
    }

    /**
     * Returns if the app has all settings available
     * @returns {boolean}
     */
    getHasExtendedSettings() {
        return !this.isLimitedVersion();
    }

    /**
     * Returns if all upgrades are available
     * @returns {boolean}
     */
    getHasExtendedUpgrades() {
        return !this.isLimitedVersion() || this.currentData.savegameV1119Imported;
    }

    /**
     * Returns if all levels & freeplay is available
     * @returns {boolean}
     */
    getHasExtendedLevelsAndFreeplay() {
        return !this.isLimitedVersion() || this.currentData.savegameV1119Imported;
    }
}

// ALEA RNG

function Mash() {
    var n = 0xefc8249d;
    return function (data) {
        data = data.toString();
        for (var i = 0; i < data.length; i++) {
            n += data.charCodeAt(i);
            var h = 0.02519603282416938 * n;
            n = h >>> 0;
            h -= n;
            h *= n;
            n = h >>> 0;
            h -= n;
            n += h * 0x100000000; // 2^32
        }
        return (n >>> 0) * 2.3283064365386963e-10; // 2^-32
    };
}

/**
 * @param {number|string} seed
 */
function makeNewRng(seed) {
    // Johannes Baage <baagoe@baagoe.com>, 2010
    var c = 1;
    var mash = Mash();
    let s0 = mash(" ");
    let s1 = mash(" ");
    let s2 = mash(" ");

    s0 -= mash(seed);
    if (s0 < 0) {
        s0 += 1;
    }
    s1 -= mash(seed);
    if (s1 < 0) {
        s1 += 1;
    }
    s2 -= mash(seed);
    if (s2 < 0) {
        s2 += 1;
    }
    mash = null;

    var random = function () {
        var t = 2091639 * s0 + c * 2.3283064365386963e-10; // 2^-32
        s0 = s1;
        s1 = s2;
        return (s2 = t - (c = t | 0));
    };

    random.exportState = function () {
        return [s0, s1, s2, c];
    };

    random.importState = function (i) {
        s0 = +i[0] || 0;
        s1 = +i[1] || 0;
        s2 = +i[2] || 0;
        c = +i[3] || 0;
    };

    return random;
}

export class RandomNumberGenerator {
    /**
     *
     * @param {number|string=} seed
     */
    constructor(seed) {
        this.internalRng = makeNewRng(seed || Math.random());
    }

    /**
     * Re-seeds the generator
     * @param {number|string} seed
     */
    reseed(seed) {
        this.internalRng = makeNewRng(seed || Math.random());
    }

    /**
     * @returns {number} between 0 and 1
     */
    next() {
        return this.internalRng();
    }

    /**
     * Random choice of an array
     * @param {array} array
     */
    choice(array) {
        const index = this.nextIntRange(0, array.length);
        return array[index];
    }

    /**
     * @param {number} min
     * @param {number} max
     * @returns {number} Integer in range [min, max[
     */
    nextIntRange(min, max) {
        assert(Number.isFinite(min), "Minimum is no integer");
        assert(Number.isFinite(max), "Maximum is no integer");
        assert(max > min, "rng: max <= min");
        return Math.floor(this.next() * (max - min) + min);
    }

    /**
     * @param {number} min
     * @param {number} max
     * @returns {number} Number in range [min, max[
     */
    nextRange(min, max) {
        assert(max > min, "rng: max <= min");
        return this.next() * (max - min) + min;
    }

    /**
     * Updates the seed
     * @param {number} seed
     */
    setSeed(seed) {
        this.internalRng = makeNewRng(seed);
    }
}

import { createHash } from "rusha";
import crc32 from "crc/crc32";
import { decompressX64 } from "./lzstring";

export function sha1(str) {
    return createHash().update(str).digest("hex");
}

// Window.location.host
export function getNameOfProvider() {
    return window[decompressX64("DYewxghgLgliB2Q")][decompressX64("BYewzgLgdghgtgUyA")];
}

// Distinguish legacy crc prefixes
export const CRC_PREFIX = "crc32".padEnd(32, "-");

/**
 * Computes the crc for a given string
 * @param {string} str
 */
export function computeCrc(str) {
    return CRC_PREFIX + crc32(str).toString(16).padStart(8, "0");
