        MetaLeverBuilding,
        MetaLogicGateBuilding,
        MetaMinerBuilding,
        MetaMixerBuilding,
        MetaPainterBuilding,
        MetaReaderBuilding,
        MetaRotaterBuilding,
        MetaStackerBuilding,
        MetaStorageBuilding,
        MetaTransistorBuilding,
        MetaTrashBuilding,
        MetaUndergroundBeltBuilding,
        MetaVirtualProcessorBuilding,
